import React, { useState } from "react";
import Footer from "../layout/footer";
import Header from "../layout/header";
import "./form.css";
import Select from "react-select";
import { toast } from "react-toastify";

export default function Form() {
  const [selectedFile, setSelectedFile] = useState("Select file *Pdf only");
  const [file, setFile] = useState();
  function handleChange(e) {
    console.log("File if not uploaded is ", file);
    const fileName = e.target.files[0].name;
    const fileType = e.target.files[0].type;

    if (fileType === "application/pdf") {
      setFile(URL.createObjectURL(e.target.files[0]));
      setSelectedFile(fileName);
      toast.success("File PDF selected successfully");
      console.log("File if uploaded  is ", file);
    } else {
      toast.error("Please Select PDF file only.");
    }
  }

  const handleSubmission = () => {};

  const options = [
    { value: "React-Js Developer", label: "React-Js Developer " },
    { value: "Node-Js Developer", label: "Node-Js Developer" },
    { value: "MERN Stack Developer", label: "MERN Stack Developer" },
  ];
  return (
    <>
      <Header />
      <section className="ap-frm">
        <div className="container">
          <div className="fm">
            <form>
              <div className="form-group">
                <label htmlfor="exampleInputEmail1">Full Name</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="First and last name"
                />
              </div>
              <div className="form-group">
                <label htmlfor="exampleInputEmail1">Email</label>
                <input
                  type="email"
                  className="form-control"
                  placeholder="Enter Email Address"
                />
              </div>

              <div className="form-group">
                <label htmlfor="exampleInputEmail1">Address</label>
                <input
                  type="date"
                  className="form-control"
                  placeholder="Enter Address here"
                />
              </div>
              <div className="form-group">
                <label htmlfor="exampleInputEmail1">Place of birth</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Enter your place of birth"
                />
              </div>
              <div className="form-group">
                <label className="jj" htmlfor="exampleInputEmail1">
                  Select Your Job
                </label>
                <Select className="selection" options={options} />
              </div>
              <div className="form-group file file--upload">
                <span className="sf">Upload Your CV</span>
                <label htmlFor="input-file">{selectedFile}</label>
                <input
                  id="input-file"
                  placeholder="file khaan hai"
                  onChange={handleChange}
                  type="file"
                />
              </div>
              <div className="butoo">
                <button
                  type="submit"
                  onClick={handleSubmission}
                  className="pst-btn2 btn btn-primary mb-2"
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </section>
      <Footer />
    </>
  );
}
