import React, { useState } from "react";
import Footer from "../layout/footer";
import Header from "../layout/header";
import "./form.css";
import Select from "react-select";
import { toast } from "react-toastify";

export default function Form() {
  const [selectedFile, setSelectedFile] = useState("Select file *Pdf only");
  const [formValues, setFormValues] = useState({name: "", email: "", date: "", placeofbirth: "", jobRole: ""})
  const [file, setFile] = useState();
  function handleChange(e) {
    setFormValues({...formValues,
    [e.target.name]: e.target.value
   })
    console.log("File if not uploaded is ", file);
    const fileName = e.target.files[0].name;
    const fileType = e.target.files[0].type;

    if (fileType === "application/pdf") {
      setFile(URL.createObjectURL(e.target.files[0]));
      setSelectedFile(fileName);
      toast.success("File PDF selected successfully");
      console.log("File if uploaded  is ", file);
    } else {
      return toast.error("Please Select PDF file only.");
    }
  }

  const handleSubmission = async(e) => {
    e.preventDefault();
    var formdata = new FormData();
    formdata.append("name",formValues.name)
    formdata.append("email",formValues.email)
    formdata.append("date",formValues.date)
    formdata.append("jobRole",formValues.jobRole)
    formdata.append("placeofbirth",formValues.placeofbirth)
    formdata.append("file",file)
    var formSubmit = await fetch(`http://localhost:4013/api/create`,{
      method: 'Post',
      bode: formdata
    })
    formSubmit = formSubmit.json();
    if(formSubmit){
      return alert("done")
    }
  };

  const options = [
    { value: "React-Js Developer", label: "React-Js Developer " },
    { value: "Node-Js Developer", label: "Node-Js Developer" },
    { value: "MERN Stack Developer", label: "MERN Stack Developer" },
  ];
  return (
    <>
      <Header />
      <section className="ap-frm">
        <div className="container">
          <div className="fm">
            <form onSubmit = {handleSubmission}>
              <div className="form-group">
                <label htmlfor="exampleInputEmail1">Full Name</label>
                <input
                  type="text"
                  name = "name"
                  value = {formValues.name}
                  className="form-control"
                  placeholder="Name"
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <label htmlfor="exampleInputEmail1">Email</label>
                <input
                  type="email"
                  name = "email"
                  value = {formValues.email}
                  className="form-control"
                  placeholder="Enter Email Address"
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label htmlfor="exampleInputEmail1">Date</label>
                <input
                  type="date"
                  name= "date"
                  value = {formValues.date}
                  className="form-control"
                  placeholder="Enter Address here"
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <label htmlfor="exampleInputEmail1">Place of birth</label>
                <input
                  type="text"
                  className="form-control"
                  value = {formValues.placeofbirth}
                  placeholder="Enter your place of birth"
                  onChange={handleChange}
                  name = "placeofbirth"
                />
              </div>
              <div className="form-group">
                <label className="jj" htmlfor="exampleInputEmail1">
                  Select Your Job
                </label>
                <Select value = {formValues.jobRole} name = "jobRole" onChange={handleChange} className="selection" options={options} />
              </div>
              <div className="form-group file file--upload">
                <span className="sf">Upload Your CV</span>
                <label htmlFor="input-file">{selectedFile}</label>
                <input
                  id="input-file"
                  placeholder="file khaan hai"
                  onChange={handleChange}
                  type="file"
                />
              </div>
              <div className="butoo">
                <button
                  type="submit"

                  className="pst-btn2 btn btn-primary mb-2"
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </section>
      <Footer />
    </>
  );
}
